import React, { createRef } from 'react'
import Chart from 'chart.js'

function LineGraph() {

    const charRef = createRef();

    const myChartRef = this.charRef.current.getContext("2d");

    const paramter = {
        type: "line",
        data: {
            labels: ["Jan", "Feb", "March"],
            datasets: [
                {
                    label: "Sales",
                    data: [86, 67, 91],
                }
            ]
        },
        options: {

        }
    }
    new Chart(myChartRef, paramter)

    return (
        <div>
            <canvas id="myChart" ref={this.charRef} />
        </div>
    )

}

export default LineGraph();
